import { useState, useRef, useEffect } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { Avatar, AvatarFallback } from "@/components/ui/avatar";
import { Badge } from "@/components/ui/badge";
import { Send, X, Loader2 } from "lucide-react";
import { messagesApi } from "@/lib/api";
import { useToast } from "@/hooks/use-toast";

interface Message {
  id: string;
  text: string;
  senderId: string;
  receiverId: string;
  listingId: string;
  createdAt: string;
}

interface ChatInterfaceProps {
  isOpen: boolean;
  onClose: () => void;
  listing: {
    id: string;
    title: string;
    provider: {
      id: string;
      name: string;
    };
  };
  otherUser: {
    id: string;
    name: string;
  };
  currentUserId: string;
  userType: "provider" | "ngo";
}

export default function ChatInterface({ 
  isOpen, 
  onClose, 
  listing,
  otherUser,
  currentUserId,
  userType
}: ChatInterfaceProps) {
  const [messages, setMessages] = useState<Message[]>([]);
  const [newMessage, setNewMessage] = useState("");
  const [loading, setLoading] = useState(false);
  const [sending, setSending] = useState(false);
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const { toast } = useToast();

  // Load messages when chat opens
  useEffect(() => {
    if (isOpen && listing?.id) {
      loadMessages();
    }
  }, [isOpen, listing?.id]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const loadMessages = async () => {
    if (!listing?.id || !otherUser?.id) {
      console.error("Missing listing or user data for loading messages");
      return;
    }

    setLoading(true);
    try {
      const response = await messagesApi.getByListing(listing.id, otherUser.id) as Message[];
      setMessages(response);
    } catch (error: any) {
      console.error("Load messages error:", error);
      toast({
        title: "Error",
        description: "Failed to load messages",
        variant: "destructive",
      });
    } finally {
      setLoading(false);
    }
  };

  const handleSendMessage = async () => {
    if (!newMessage.trim() || sending) return;

    // Validate required data before sending
    if (!otherUser?.id || !listing?.id) {
      toast({
        title: "Error",
        description: "Unable to send message - missing chat information",
        variant: "destructive",
      });
      return;
    }

    setSending(true);
    try {
      const messageData = {
        text: newMessage.trim(),
        receiverId: otherUser.id,
        listingId: listing.id
      };

      const newMsg = await messagesApi.send(messageData) as Message;
      setMessages(prev => [...prev, newMsg]);
      setNewMessage("");
    } catch (error: any) {
      console.error("Message send error:", error);
      toast({
        title: "Error",
        description: error.message || "Failed to send message",
        variant: "destructive",
      });
    } finally {
      setSending(false);
    }
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  if (!isOpen) return null;

  const formatMessageTime = (createdAt: string) => {
    return new Date(createdAt).toLocaleTimeString([], { 
      hour: '2-digit', 
      minute: '2-digit' 
    });
  };

  return (
    <div className="fixed inset-0 bg-black/50 flex items-center justify-center z-50 p-4">
      <Card className="w-full max-w-2xl h-[600px] flex flex-col">
        <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-4 border-b">
          <div className="flex items-center gap-3">
            <Avatar>
              <AvatarFallback>{otherUser.name.charAt(0)}</AvatarFallback>
            </Avatar>
            <div>
              <CardTitle className="text-lg" data-testid="text-chat-partner">{otherUser.name}</CardTitle>
              <Badge variant="outline" className="text-xs" data-testid="badge-listing-title">
                {listing.title}
              </Badge>
            </div>
          </div>
          <div className="flex gap-2">
            <Button variant="ghost" size="icon" onClick={onClose} data-testid="button-close-chat">
              <X className="w-4 h-4" />
            </Button>
          </div>
        </CardHeader>

        <CardContent className="flex-1 p-0">
          {/* Messages */}
          <div className="flex-1 overflow-y-auto p-4 space-y-4 h-[400px]">
            {loading ? (
              <div className="flex justify-center items-center h-full">
                <Loader2 className="h-6 w-6 animate-spin" />
                <span className="ml-2">Loading messages...</span>
              </div>
            ) : messages.length === 0 ? (
              <div className="flex justify-center items-center h-full text-muted-foreground">
                <p>No messages yet. Start the conversation!</p>
              </div>
            ) : (
              messages.map((message) => (
                <div
                  key={message.id}
                  className={`flex ${message.senderId === currentUserId ? 'justify-end' : 'justify-start'}`}
                  data-testid={`message-${message.id}`}
                >
                  <div
                    className={`max-w-[80%] rounded-lg p-3 ${
                      message.senderId === currentUserId
                        ? 'bg-primary text-primary-foreground ml-auto'
                        : 'bg-muted text-muted-foreground'
                    }`}
                  >
                    <p className="text-sm">{message.text}</p>
                    <p className="text-xs opacity-70 mt-1">{formatMessageTime(message.createdAt)}</p>
                  </div>
                </div>
              ))
            )}
            <div ref={messagesEndRef} />
          </div>

          {/* Message Input */}
          <div className="border-t p-4">
            <div className="flex gap-2">
              <Input
                value={newMessage}
                onChange={(e) => setNewMessage(e.target.value)}
                onKeyPress={handleKeyPress}
                placeholder="Type your message..."
                className="flex-1"
                data-testid="input-message"
              />
              <Button 
                onClick={handleSendMessage} 
                disabled={!newMessage.trim() || sending}
                data-testid="button-send"
              >
                {sending ? (
                  <Loader2 className="w-4 h-4 animate-spin" />
                ) : (
                  <Send className="w-4 h-4" />
                )}
              </Button>
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

ZeroWasteRescue/client/src/components/Fo.content:
import { Card, CardContent, CardFooter, CardHeader } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { MapPin, Clock, Users, MessageCircle, Package } from "lucide-react";

export interface FoodListing {
  id: string;
  title: string;
  description: string;
  quantity: string;
  location: string;
  provider: string;
  timePosted: string;
  expiresIn: string;
  urgency: "low" | "medium" | "high";
  type: string;
  claimStatus?: string;
  claimedByNgoId?: string;
  providerId?: string;
}

interface FoodListingCardProps {
  listing: FoodListing;
  onContact: (id: string) => void;
  onClaim: (id: string) => void;
  onStatusUpdate?: (id: string, status: string) => void;
  userType: "provider" | "ngo";
  currentUserId?: string;
}

export default function FoodListingCard({ 
  listing, 
  onContact, 
  onClaim, 
  onStatusUpdate, 
  userType, 
  currentUserId 
}: FoodListingCardProps) {
  const urgencyColors = {
    low: "bg-green-100 text-green-800 dark:bg-green-900/20 dark:text-green-300",
    medium: "bg-amber-100 text-amber-800 dark:bg-amber-900/20 dark:text-amber-300", 
    high: "bg-red-100 text-red-800 dark:bg-red-900/20 dark:text-red-300"
  };

  const isClaimedByUser = userType === "ngo" && listing.claimedByNgoId === currentUserId;
  const canUpdateStatus = isClaimedByUser && listing.claimStatus && listing.claimStatus !== 'completed' && listing.claimStatus !== 'cancelled';

  const getStatusColor = (status?: string) => {
    switch (status) {
      case 'completed': return 'default';
      case 'cancelled': return 'destructive'; 
      case 'in_transit': return 'secondary';
      case 'confirmed': case 'in_process': case 'delivery_partner_assigned': return 'outline';
      default: return 'outline';
    }
  };

  const getStatusLabel = (status?: string) => {
    switch (status) {
      case 'claimed': return 'Claimed';
      case 'confirmed': return 'Confirmed';
      case 'in_process': return 'In Process';
      case 'delivery_partner_assigned': return 'Driver Assigned';
      case 'in_transit': return 'In Transit';
      case 'completed': return 'Completed';
      case 'cancelled': return 'Cancelled';
      default: return status;
    }
  };

  const getNextStatusOptions = (currentStatus?: string) => {
    switch (currentStatus) {
      case 'claimed':
        return [
          { value: 'confirmed', label: 'Confirm Pickup' },
          { value: 'cancelled', label: 'Cancel Order' }
        ];
      case 'confirmed':
        return [
          { value: 'in_process', label: 'Start Processing' },
          { value: 'cancelled', label: 'Cancel Order' }
        ];
      case 'in_process':
        return [
          { value: 'delivery_partner_assigned', label: 'Assign Driver' },
          { value: 'in_transit', label: 'Pick Up Food' },
          { value: 'cancelled', label: 'Cancel Order' }
        ];
      case 'delivery_partner_assigned':
        return [
          { value: 'in_transit', label: 'Start Delivery' },
          { value: 'cancelled', label: 'Cancel Order' }
        ];
      case 'in_transit':
        return [
          { value: 'completed', label: 'Mark Delivered' }
        ];
      default:
        return [];
    }
  };

  return (
    <Card className="hover-elevate" data-testid={`card-listing-${listing.id}`}>
      <CardHeader className="pb-3">
        <div className="flex justify-between items-start gap-2">
          <h3 className="font-semibold text-lg text-card-foreground" data-testid="text-listing-title">
            {listing.title}
          </h3>
          <div className="flex gap-2">
            <Badge 
              className={urgencyColors[listing.urgency]}
              data-testid="badge-urgency"
            >
              {listing.urgency} priority
            </Badge>
            {listing.claimStatus && listing.claimStatus !== 'open' && (
              <Badge 
                variant={getStatusColor(listing.claimStatus)}
                data-testid="badge-status"
              >
                {getStatusLabel(listing.claimStatus)}
              </Badge>
            )}
          </div>
        </div>
        <p className="text-muted-foreground" data-testid="text-listing-description">
          {listing.description}
        </p>
      </CardHeader>

      <CardContent className="space-y-3">
        <div className="grid grid-cols-2 gap-4 text-sm">
          <div className="flex items-center gap-2">
            <Users className="w-4 h-4 text-muted-foreground" />
            <span data-testid="text-quantity">{listing.quantity}</span>
          </div>
          <div className="flex items-center gap-2">
            <Clock className="w-4 h-4 text-muted-foreground" />
            <span data-testid="text-expires">Expires {listing.expiresIn}</span>
          </div>
        </div>

        <div className="flex items-center gap-2 text-sm">
          <MapPin className="w-4 h-4 text-muted-foreground" />
          <span data-testid="text-location">{listing.location}</span>
        </div>

        <div className="flex items-center justify-between text-sm text-muted-foreground">
          <span data-testid="text-provider">by {listing.provider}</span>
          <span data-testid="text-time-posted">{listing.timePosted}</span>
        </div>

        <Badge variant="secondary" data-testid="badge-food-type">
          {listing.type}
        </Badge>

        {/* Status Update Section for NGOs */}
        {canUpdateStatus && onStatusUpdate && (
          <div className="mt-4 p-3 bg-muted rounded-lg">
            <div className="flex items-center gap-2">
              <Package className="w-4 h-4 text-muted-foreground" />
              <span className="text-sm font-medium">Update Status:</span>
              <Select 
                value={listing.claimStatus} 
                onValueChange={(value) => onStatusUpdate(listing.id, value)}
              >
                <SelectTrigger className="w-48">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  {getNextStatusOptions(listing.claimStatus).map((option) => (
                    <SelectItem key={option.value} value={option.value}>
                      {option.label}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
          </div>
        )}
      </CardContent>

      <CardFooter className="pt-3 gap-2">
        <Button 
          variant="outline" 
          size="sm" 
          onClick={() => onContact(listing.id)}
          data-testid="button-contact"
        >
          <MessageCircle className="w-4 h-4 mr-2" />
          Chat
        </Button>
        {userType === "ngo" && listing.claimStatus === 'open' && (
          <Button 
            size="sm" 
            onClick={() => onClaim(listing.id)}
            data-testid="button-claim"
          >
            Claim Food
          </Button>
        )}
      </CardFooter>
    </Card>
  );
}

import { useState } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { Plus, X, MapPin, Clock, Utensils } from "lucide-react";

interface FoodRegistrationFormProps {
  onSubmit: (data: any) => void;
  onCancel: () => void;
}

export default function FoodRegistrationForm({ onSubmit, onCancel }: FoodRegistrationFormProps) {
  const [formData, setFormData] = useState({
    title: "",
    description: "",
    quantity: "",
    location: "",
    foodType: "",
    expiresIn: "",
    urgency: "medium",
    contactInfo: "",
    specialInstructions: ""
  });

  const [tags, setTags] = useState<string[]>([]);
  const [newTag, setNewTag] = useState("");

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    console.log("Form submitted:", { ...formData, tags });
    onSubmit({ ...formData, tags });
  };

  const addTag = () => {
    if (newTag.trim() && !tags.includes(newTag.trim())) {
      setTags([...tags, newTag.trim()]);
      setNewTag("");
    }
  };

  const removeTag = (tagToRemove: string) => {
    setTags(tags.filter(tag => tag !== tagToRemove));
  };

  return (
    <Card className="max-w-2xl mx-auto">
      <CardHeader>
        <CardTitle className="flex items-center gap-2">
          <Utensils className="w-5 h-5 text-primary" />
          Register Food for Donation
        </CardTitle>
      </CardHeader>
      <CardContent>
        <form onSubmit={handleSubmit} className="space-y-6">
          {/* Basic Information */}
          <div className="space-y-4">
            <div>
              <Label htmlFor="title">Food Title*</Label>
              <Input
                id="title"
                value={formData.title}
                onChange={(e) => setFormData({...formData, title: e.target.value})}
                placeholder="e.g., Fresh Vegetarian Meals"
                required
                data-testid="input-title"
              />
            </div>

            <div>
              <Label htmlFor="description">Description*</Label>
              <Textarea
                id="description"
                value={formData.description}
                onChange={(e) => setFormData({...formData, description: e.target.value})}
                placeholder="Describe the food items, ingredients, preparation method..."
                required
                data-testid="input-description"
              />
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <Label htmlFor="quantity">Quantity*</Label>
                <Input
                  id="quantity"
                  value={formData.quantity}
                  onChange={(e) => setFormData({...formData, quantity: e.target.value})}
                  placeholder="e.g., 25 meals, 5kg rice"
                  required
                  data-testid="input-quantity"
                />
              </div>

              <div>
                <Label htmlFor="foodType">Food Type*</Label>
                <Select value={formData.foodType} onValueChange={(value) => setFormData({...formData, foodType: value})}>
                  <SelectTrigger data-testid="select-food-type">
                    <SelectValue placeholder="Select type" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="prepared-meals">Prepared Meals</SelectItem>
                    <SelectItem value="raw-ingredients">Raw Ingredients</SelectItem>
                    <SelectItem value="packaged-food">Packaged Food</SelectItem>
                    <SelectItem value="beverages">Beverages</SelectItem>
                    <SelectItem value="baked-goods">Baked Goods</SelectItem>
                  </SelectContent>
                </Select>
              </div>
            </div>
          </div>

          {/* Location and Timing */}
          <div className="space-y-4">
            <div>
              <Label htmlFor="location" className="flex items-center gap-2">
                <MapPin className="w-4 h-4" />
                Pickup Location*
              </Label>
              <Input
                id="location"
                value={formData.location}
                onChange={(e) => setFormData({...formData, location: e.target.value})}
                placeholder="Full address including landmark"
                required
                data-testid="input-location"
              />
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <Label htmlFor="expiresIn" className="flex items-center gap-2">
                  <Clock className="w-4 h-4" />
                  Expires In*
                </Label>
                <Select value={formData.expiresIn} onValueChange={(value) => setFormData({...formData, expiresIn: value})}>
                  <SelectTrigger data-testid="select-expires">
                    <SelectValue placeholder="Select timeframe" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="2-hours">2 hours</SelectItem>
                    <SelectItem value="4-hours">4 hours</SelectItem>
                    <SelectItem value="6-hours">6 hours</SelectItem>
                    <SelectItem value="12-hours">12 hours</SelectItem>
                    <SelectItem value="1-day">1 day</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div>
                <Label htmlFor="urgency">Priority Level*</Label>
                <Select value={formData.urgency} onValueChange={(value) => setFormData({...formData, urgency: value})}>
                  <SelectTrigger data-testid="select-urgency">
                    <SelectValue placeholder="Select priority" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="low">Low - Flexible timing</SelectItem>
                    <SelectItem value="medium">Medium - Same day</SelectItem>
                    <SelectItem value="high">High - Urgent pickup</SelectItem>
                  </SelectContent>
                </Select>
              </div>
            </div>
          </div>

          {/* Tags */}
          <div className="space-y-3">
            <Label>Tags (Optional)</Label>
            <div className="flex gap-2">
              <Input
                value={newTag}
                onChange={(e) => setNewTag(e.target.value)}
                placeholder="Add tags like 'halal', 'vegan'..."
                onKeyDown={(e) => e.key === 'Enter' && (e.preventDefault(), addTag())}
                data-testid="input-tag"
              />
              <Button type="button" variant="outline" size="icon" onClick={addTag} data-testid="button-add-tag">
                <Plus className="w-4 h-4" />
              </Button>
            </div>
            {tags.length > 0 && (
              <div className="flex flex-wrap gap-2">
                {tags.map((tag, index) => (
                  <Badge key={index} variant="secondary" className="flex items-center gap-1">
                    {tag}
                    <button
                      type="button"
                      onClick={() => removeTag(tag)}
                      className="ml-1 hover:bg-destructive hover:text-destructive-foreground rounded-full"
                      data-testid={`button-remove-tag-${index}`}
                    >
                      <X className="w-3 h-3" />
                    </button>
                  </Badge>
                ))}
              </div>
            )}
          </div>

