              />
            </div>

            <div className="flex gap-2 pt-4">
              <Button 
                onClick={handleSaveEdit} 
                disabled={updateListingMutation.isPending}
                className="flex-1"
              >
                {updateListingMutation.isPending ? "Saving..." : "Save Changes"}
              </Button>
              <Button 
                variant="outline" 
                onClick={() => setEditingListing(null)}
